function counterUp(t,{duration:n=1e3}={}){let e=t.textContent.trim().match(/^([^0-9]*)([0-9.,]+)([^0-9]*)$/);if(!e)return;let o=e[1],r=e[2].replace(/,/g,""),c=e[3],i=parseFloat(r),l=performance.now();function $(e){let r=Math.min((e-l)/n,1);t.textContent=o+Math.floor(r*i).toLocaleString()+c,r<1&&requestAnimationFrame($)}requestAnimationFrame($)}